import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Header } from "./components/Header"
import {Meat} from "./components/Meat";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            logged_in: false,
            meat_content: "home"
        };
    }

    handleClick(mc) {
        this.setState({meat_content: mc});
    }

    render() {
        return(
            <div>
                <Header logged_in={this.state.logged_in} onReq={(mc) => this.handleClick(mc)}/>
                <Meat meat_content={this.state.meat_content}/>
            </div>
        );
    }
}

export { App };
